mapper.insert:
insert into keyword (keyword_content, moviemap_flag, resourcelist_flag
  )
values (#{keywordContent,jdbcType=CHAR}, #{moviemapFlag,jdbcType=INTEGER}, #{resourcelistFlag,jdbcType=INTEGER}
  )

mapper.update:
update keyword
set moviemap_flag = #{moviemapFlag,jdbcType=INTEGER},
  resourcelist_flag = #{resourcelistFlag,jdbcType=INTEGER}
where keyword_content = #{keywordContent,jdbcType=CHAR}

mapper.select:
select keyword_content, moviemap_flag, resourcelist_flag
from keyword
where keyword_content = #{keywordContent,jdbcType=CHAR}
